server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import { storage } from "./storage";
import { processPdfFile, searchDocuments } from "./services/pdfProcessor";
import { generateChatResponse, generateTitle } from "./services/gemini";
import { vectorStore } from "./services/vectorStore";
import { insertMessageSchema } from "@shared/schema";
import { z } from "zod";

// Configure multer for file uploads
const upload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    if (file.mimetype === 'application/pdf') {
      cb(null, true);
    } else {
      cb(new Error('Only PDF files are allowed'), false);
    }
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Upload PDF endpoint
  app.post("/api/upload", upload.single("file"), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: "No file uploaded" });
      }

      const filename = `${Date.now()}-${req.file.originalname}`;
      const documentId = await processPdfFile(
        req.file.buffer,
        filename,
        req.file.originalname
      );

      res.json({ 
        success: true, 
        documentId,
        message: "File uploaded and processing started"
      });
    } catch (error) {
      console.error("Upload error:", error);
      res.status(500).json({ error: "Failed to upload file" });
    }
  });

  // Get all documents
  app.get("/api/documents", async (req, res) => {
    try {
      const documents = await storage.getAllDocuments();
      const documentsWithStatus = documents.map(doc => ({
        id: doc.id,
        filename: doc.originalName,
        status: doc.status,
        fileSize: doc.fileSize,
        totalPages: doc.totalPages,
        totalChunks: doc.totalChunks,
        uploadedAt: doc.uploadedAt,
        processedAt: doc.processedAt,
      }));

      res.json(documentsWithStatus);
    } catch (error) {
      console.error("Error fetching documents:", error);
      res.status(500).json({ error: "Failed to fetch documents" });
    }
  });

  // Delete document
  app.delete("/api/documents/:id", async (req, res) => {
    try {
      await storage.deleteDocument(req.params.id);
      res.json({ success: true });
    } catch (error) {
      console.error("Error deleting document:", error);
      res.status(500).json({ error: "Failed to delete document" });
    }
  });

  // Chat endpoint
  app.post("/api/chat", async (req, res) => {
    try {
      const { message, conversationId } = req.body;

      if (!message || typeof message !== 'string') {
        return res.status(400).json({ error: "Message is required" });
      }

      // Create or get conversation
      let conversation;
      if (conversationId) {
        conversation = await storage.getConversation(conversationId);
        if (!conversation) {
          return res.status(404).json({ error: "Conversation not found" });
        }
      } else {
        conversation = await storage.createConversation({
          title: null,
        });
      }

      // Save user message
      await storage.createMessage({
        conversationId: conversation.id,
        role: "user",
        content: message,
        sources: null,
      });

      // Search for relevant documents
      const searchResults = await searchDocuments(message, 5);

      // Prepare context and sources
      const context = searchResults
        .map(result => `Document: ${result.document?.originalName}\nContent: ${result.chunk.content}`)
        .join('\n\n');

      const sources = searchResults.map(result => ({
        documentId: result.chunk.documentId,
        documentName: result.document?.originalName || "Unknown",
        pageNumber: result.chunk.pageNumber,
        excerpt: result.chunk.content.substring(0, 200) + "...",
      }));

      // Generate AI response
      const aiResponse = await generateChatResponse(message, context, sources);

      // Save AI message
      const aiMessage = await storage.createMessage({
        conversationId: conversation.id,
        role: "assistant",
        content: aiResponse,
        sources: sources.length > 0 ? sources : null,
      });

      // Generate title for new conversations
      if (!conversation.title) {
        const title = await generateTitle(message);
        await storage.updateConversationTitle(conversation.id, title);
      }

      res.json({
        conversationId: conversation.id,
        message: {
          id: aiMessage.id,
          role: aiMessage.role,
          content: aiMessage.content,
          sources: aiMessage.sources,
          createdAt: aiMessage.createdAt,
        },
      });
    } catch (error) {
      console.error("Chat error:", error);
      res.status(500).json({ error: "Failed to process chat message" });
    }
  });

  // Get conversation messages
  app.get("/api/conversations/:id/messages", async (req, res) => {
    try {
      const messages = await storage.getConversationMessages(req.params.id);
      const formattedMessages = messages.map(msg => ({
        id: msg.id,
        role: msg.role,
        content: msg.content,
        sources: msg.sources,
        createdAt: msg.createdAt,
      }));

      res.json(formattedMessages);
    } catch (error) {
      console.error("Error fetching messages:", error);
      res.status(500).json({ error: "Failed to fetch messages" });
    }
  });

  // Get all conversations
  app.get("/api/conversations", async (req, res) => {
    try {
      const conversations = await storage.getAllConversations();
      res.json(conversations);
    } catch (error) {
      console.error("Error fetching conversations:", error);
      res.status(500).json({ error: "Failed to fetch conversations" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type Document, type InsertDocument, type DocumentChunk, type InsertDocumentChunk, type Conversation, type InsertConversation, type Message, type InsertMessage, type DocumentProcessingStatus } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Document operations
  createDocument(document: InsertDocument): Promise<Document>;
  getDocument(id: string): Promise<Document | undefined>;
  getAllDocuments(): Promise<Document[]>;
  updateDocumentStatus(id: string, status: string, metadata?: any): Promise<void>;
  deleteDocument(id: string): Promise<void>;

  // Document chunk operations
  createDocumentChunk(chunk: InsertDocumentChunk): Promise<DocumentChunk>;
  getDocumentChunks(documentId: string): Promise<DocumentChunk[]>;
  searchChunksByEmbedding(embedding: number[], limit?: number): Promise<DocumentChunk[]>;

  // Conversation operations
  createConversation(conversation: InsertConversation): Promise<Conversation>;
  getConversation(id: string): Promise<Conversation | undefined>;
  getAllConversations(): Promise<Conversation[]>;
  updateConversationTitle(id: string, title: string): Promise<void>;

  // Message operations
  createMessage(message: InsertMessage): Promise<Message>;
  getConversationMessages(conversationId: string): Promise<Message[]>;
}

export class MemStorage implements IStorage {
  private documents: Map<string, Document>;
  private documentChunks: Map<string, DocumentChunk>;
  private conversations: Map<string, Conversation>;
  private messages: Map<string, Message>;

  constructor() {
    this.documents = new Map();
    this.documentChunks = new Map();
    this.conversations = new Map();
    this.messages = new Map();
  }

  async createDocument(insertDocument: InsertDocument): Promise<Document> {
    const id = randomUUID();
    const document: Document = {
      ...insertDocument,
      id,
      uploadedAt: new Date(),
      processedAt: null,
    };
    this.documents.set(id, document);
    return document;
  }

  async getDocument(id: string): Promise<Document | undefined> {
    return this.documents.get(id);
  }

  async getAllDocuments(): Promise<Document[]> {
    return Array.from(this.documents.values()).sort((a, b) => 
      new Date(b.uploadedAt).getTime() - new Date(a.uploadedAt).getTime()
    );
  }

  async updateDocumentStatus(id: string, status: string, metadata?: any): Promise<void> {
    const document = this.documents.get(id);
    if (document) {
      document.status = status;
      if (status === "completed") {
        document.processedAt = new Date();
      }
      if (metadata) {
        document.metadata = { ...document.metadata, ...metadata };
        if (metadata.totalPages) document.totalPages = metadata.totalPages;
        if (metadata.totalChunks) document.totalChunks = metadata.totalChunks;
      }
      this.documents.set(id, document);
    }
  }

  async deleteDocument(id: string): Promise<void> {
    this.documents.delete(id);
    // Delete associated chunks
    for (const [chunkId, chunk] of this.documentChunks.entries()) {
      if (chunk.documentId === id) {
        this.documentChunks.delete(chunkId);
      }
    }
  }

  async createDocumentChunk(insertChunk: InsertDocumentChunk): Promise<DocumentChunk> {
    const id = randomUUID();
    const chunk: DocumentChunk = {
      ...insertChunk,
      id,
    };
    this.documentChunks.set(id, chunk);
    return chunk;
  }

  async getDocumentChunks(documentId: string): Promise<DocumentChunk[]> {
    return Array.from(this.documentChunks.values())
      .filter(chunk => chunk.documentId === documentId)
      .sort((a, b) => a.chunkIndex - b.chunkIndex);
  }

  async searchChunksByEmbedding(embedding: number[], limit: number = 5): Promise<DocumentChunk[]> {
    // Simple cosine similarity for in-memory storage
    const chunksWithScores = Array.from(this.documentChunks.values())
      .filter(chunk => chunk.embedding)
      .map(chunk => ({
        chunk,
        score: this.cosineSimilarity(embedding, chunk.embedding as number[])
      }))
      .sort((a, b) => b.score - a.score)
      .slice(0, limit);

    return chunksWithScores.map(item => item.chunk);
  }

  private cosineSimilarity(a: number[], b: number[]): number {
    const dotProduct = a.reduce((sum, ai, i) => sum + ai * b[i], 0);
    const magnitudeA = Math.sqrt(a.reduce((sum, ai) => sum + ai * ai, 0));
    const magnitudeB = Math.sqrt(b.reduce((sum, bi) => sum + bi * bi, 0));
    return dotProduct / (magnitudeA * magnitudeB);
  }

  async createConversation(insertConversation: InsertConversation): Promise<Conversation> {
    const id = randomUUID();
    const conversation: Conversation = {
      ...insertConversation,
      id,
      createdAt: new Date(),
      lastMessageAt: new Date(),
    };
    this.conversations.set(id, conversation);
    return conversation;
  }

  async getConversation(id: string): Promise<Conversation | undefined> {
    return this.conversations.get(id);
  }

  async getAllConversations(): Promise<Conversation[]> {
    return Array.from(this.conversations.values()).sort((a, b) => 
      new Date(b.lastMessageAt).getTime() - new Date(a.lastMessageAt).getTime()
    );
  }

  async updateConversationTitle(id: string, title: string): Promise<void> {
    const conversation = this.conversations.get(id);
    if (conversation) {
      conversation.title = title;
      this.conversations.set(id, conversation);
    }
  }

  async createMessage(insertMessage: InsertMessage): Promise<Message> {
    const id = randomUUID();
    const message: Message = {
      ...insertMessage,
      id,
      createdAt: new Date(),
    };
    this.messages.set(id, message);

    // Update conversation last message time
    const conversation = this.conversations.get(insertMessage.conversationId);
    if (conversation) {
      conversation.lastMessageAt = new Date();
      this.conversations.set(insertMessage.conversationId, conversation);
    }

    return message;
  }

  async getConversationMessages(conversationId: string): Promise<Message[]> {
    return Array.from(this.messages.values())
      .filter(message => message.conversationId === conversationId)
      .sort((a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime());
  }
}

export const storage = new MemStorage();

