  --muted-foreground: hsl(217.9 10.6% 64.9%);
  --accent: hsl(215 27.9% 16.9%);
  --accent-foreground: hsl(210 20% 98%);
  --destructive: hsl(0 62.8% 30.6%);
  --destructive-foreground: hsl(210 20% 98%);
  --border: hsl(215 27.9% 16.9%);
  --input: hsl(215 27.9% 16.9%);
  --ring: hsl(212.7 26.8% 83.9%);
  --ai-primary: hsl(217 91% 60%);
  --ai-secondary: hsl(270 95% 75%);
  --success: hsl(142 76% 36%);
  --warning: hsl(38 92% 50%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
  }
}

@layer components {
  .ai-gradient {
    background: linear-gradient(135deg, hsl(var(--ai-primary)) 0%, hsl(var(--ai-secondary)) 100%);
  }

  .chat-bubble-user {
    @apply bg-primary text-primary-foreground;
  }

  .chat-bubble-ai {
    @apply bg-card border border-border;
  }

  .status-processing {
    background-color: hsl(var(--warning) / 0.2);
    color: hsl(var(--warning));
    border-color: hsl(var(--warning) / 0.3);
  }

  .status-completed {
    background-color: hsl(var(--success) / 0.2);
    color: hsl(var(--success));
    border-color: hsl(var(--success) / 0.3);
  }

  .status-error {
    background-color: hsl(var(--destructive) / 0.2);
    color: hsl(var(--destructive));
    border-color: hsl(var(--destructive) / 0.3);
  }

  .upload-area {
    border: 2px dashed hsl(var(--muted-foreground) / 0.3);
    background-color: hsl(var(--muted) / 0.2);
    transition: all 0.2s ease-in-out;
  }

  .upload-area:hover {
    border-color: hsl(var(--primary) / 0.5);
    background-color: hsl(var(--muted) / 0.3);
  }

  .upload-area.drag-over {
    border-color: hsl(var(--primary));
    background-color: hsl(var(--primary) / 0.1);
  }

  .btn-primary {
    @apply bg-primary text-primary-foreground hover:bg-primary/90;
  }

  .btn-ai {
    background: linear-gradient(135deg, hsl(var(--ai-primary)) 0%, hsl(var(--ai-secondary)) 100%);
    @apply text-white hover:opacity-90;
  }
}

