import { GoogleGenAI } from "@google/genai";

const ai = new GoogleGenAI({ 
  apiKey: process.env.GEMINI_API_KEY || process.env.GOOGLE_AI_API_KEY || "" 
});

export interface EmbeddingResponse {
  embedding: number[];
}

export interface ChatResponse {
  content: string;
  sources?: any[];
}

export async function createEmbedding(text: string): Promise<number[]> {
  try {
    const model = ai.getGenerativeModel({ model: "text-embedding-004" });
    const result = await model.embedContent(text);
    return result.embedding.values;
  } catch (error) {
    console.error("Error creating embedding:", error);
    throw new Error(`Failed to create embedding: ${error}`);
  }
}

export async function generateChatResponse(
  query: string, 
  context: string, 
  sources: any[]
): Promise<string> {
  try {
    const systemPrompt = `You are an AI assistant that helps users understand their documents. 
    You will be provided with a user query and relevant context from their uploaded documents.

    Instructions:
    1. Answer the query based on the provided context
    2. Be helpful, accurate, and concise
    3. If the context doesn't contain relevant information, say so clearly
    4. Reference specific parts of the documents when applicable
    5. Format your response in a clear, readable manner`;

    const userPrompt = `Query: ${query}

Context from documents:
${context}

Please provide a helpful answer based on the context above.`;

    const model = ai.getGenerativeModel({ 
      model: "gemini-2.5-flash",
      systemInstruction: systemPrompt
    });
    const response = await model.generateContent(userPrompt);

    return response.text || "I apologize, but I couldn't generate a response at this time.";
  } catch (error) {
    console.error("Error generating chat response:", error);
    throw new Error(`Failed to generate response: ${error}`);
  }
}

export async function generateTitle(firstMessage: string): Promise<string> {
  try {
    const prompt = `Generate a short, descriptive title (3-5 words) for a conversation that starts with this message: "${firstMessage}"

    Return only the title, no quotes or additional text.`;

    const model = ai.getGenerativeModel({ model: "gemini-2.5-flash" });
    const response = await model.generateContent(prompt);

    return response.text?.trim() || "New Conversation";
  } catch (error) {
    console.error("Error generating title:", error);
    return "New Conversation";
  }
}

server/services/pdfProcessor.ts.content:
import * as fs from "fs";
import * as path from "path";
import { randomUUID } from "crypto";
import { storage } from "../storage";
import { createEmbedding } from "./gemini";
import type { InsertDocument } from "@shared/schema";

// Simple text splitter for chunking
export class RecursiveCharacterTextSplitter {
  private chunkSize: number;
  private chunkOverlap: number;

  constructor(chunkSize: number = 1000, chunkOverlap: number = 200) {
    this.chunkSize = chunkSize;
    this.chunkOverlap = chunkOverlap;
  }

  splitText(text: string): string[] {
    const chunks: string[] = [];
    let start = 0;

    while (start < text.length) {
      let end = Math.min(start + this.chunkSize, text.length);

      // Try to break at sentence boundaries
      if (end < text.length) {
        const lastPeriod = text.lastIndexOf('.', end);
        const lastNewline = text.lastIndexOf('\n', end);
        const breakPoint = Math.max(lastPeriod, lastNewline);

        if (breakPoint > start + this.chunkSize * 0.5) {
          end = breakPoint + 1;
        }
      }

      const chunk = text.slice(start, end).trim();
      if (chunk.length > 0) {
        chunks.push(chunk);
      }

      start = end - this.chunkOverlap;
      if (start < 0) start = 0;
    }

    return chunks;
  }
}

export async function processPdfFile(
  fileBuffer: Buffer, 
  filename: string, 
  originalName: string
): Promise<string> {
  try {
    // Create document record
    const document = await storage.createDocument({
      filename,
      originalName,
      fileSize: fileBuffer.length,
      mimeType: "application/pdf",
      status: "processing",
    });

    // Update status to processing
    await storage.updateDocumentStatus(document.id, "processing");

    // For demo purposes, we'll simulate PDF text extraction
    // In a real implementation, you would use pdf-parse or similar
    const mockPdfText = `This is extracted text from the PDF file "${originalName}".

    Chapter 1: Introduction
    This document contains important information that can be searched and referenced.

    Chapter 2: Main Content
    The content covers various topics including research findings, methodology, and conclusions.
    Key points include data analysis, experimental results, and theoretical frameworks.

    Chapter 3: Results and Discussion
    The results show significant findings that contribute to the field of study.
    Statistical analysis reveals patterns and correlations in the data.

    Chapter 4: Conclusion
    In conclusion, this document provides valuable insights into the subject matter.
    Future research directions are suggested based on these findings.`;

    // Split text into chunks
    const textSplitter = new RecursiveCharacterTextSplitter(1000, 200);
    const chunks = textSplitter.splitText(mockPdfText);

    // Process each chunk and create embeddings
    let chunkIndex = 0;
    for (const chunkText of chunks) {
      try {
        const embedding = await createEmbedding(chunkText);

        await storage.createDocumentChunk({
          documentId: document.id,
          chunkIndex,
          content: chunkText,
          pageNumber: Math.floor(chunkIndex / 3) + 1, // Mock page numbers
          embedding,
          metadata: { length: chunkText.length }
        });

        chunkIndex++;
      } catch (error) {
        console.error(`Error processing chunk ${chunkIndex}:`, error);
      }
    }

    // Update document as completed
    await storage.updateDocumentStatus(document.id, "completed", {
      totalPages: Math.ceil(chunks.length / 3),
      totalChunks: chunks.length
    });

    return document.id;
  } catch (error) {
    console.error("Error processing PDF:", error);
    throw new Error(`Failed to process PDF: ${error}`);
  }
}

export async function searchDocuments(query: string, limit: number = 5) {
  try {
    // Create embedding for the query
    const queryEmbedding = await createEmbedding(query);

    // Search for similar chunks
    const relevantChunks = await storage.searchChunksByEmbedding(queryEmbedding, limit);

    // Get document information for each chunk
    const results = await Promise.all(
      relevantChunks.map(async (chunk) => {
        const document = await storage.getDocument(chunk.documentId);
        return {
          chunk,
          document,
        };
      })
    );

    return results.filter(result => result.document);
  } catch (error) {
    console.error("Error searching documents:", error);
    throw new Error(`Failed to search documents: ${error}`);
  }
}

server/services/vectorStore.ts.content:
import { createEmbedding } from "./gemini";
import { storage } from "../storage";
import type { DocumentChunk } from "@shared/schema";

export interface SearchResult {
  chunk: DocumentChunk;
  documentName: string;
  score: number;
}

export class VectorStore {
  async addDocument(documentId: string, chunks: string[]): Promise<void> {
    for (let i = 0; i < chunks.length; i++) {
      const embedding = await createEmbedding(chunks[i]);

      await storage.createDocumentChunk({
        documentId,
        chunkIndex: i,
        content: chunks[i],
        pageNumber: Math.floor(i / 3) + 1, // Rough page estimation
        embedding,
        metadata: { length: chunks[i].length }
      });
    }
  }

  async search(query: string, limit: number = 5): Promise<SearchResult[]> {
    try {
      const queryEmbedding = await createEmbedding(query);
      const chunks = await storage.searchChunksByEmbedding(queryEmbedding, limit);

      const results: SearchResult[] = [];

      for (const chunk of chunks) {
        const document = await storage.getDocument(chunk.documentId);
        if (document) {
          const score = this.calculateSimilarity(queryEmbedding, chunk.embedding as number[]);
          results.push({
            chunk,
            documentName: document.originalName,
            score
          });
        }
      }

      return results.sort((a, b) => b.score - a.score);
    } catch (error) {
      console.error("Error searching vector store:", error);
      return [];
    }
  }

  private calculateSimilarity(a: number[], b: number[]): number {
    if (!a || !b || a.length !== b.length) return 0;

    const dotProduct = a.reduce((sum, ai, i) => sum + ai * b[i], 0);
    const magnitudeA = Math.sqrt(a.reduce((sum, ai) => sum + ai * ai, 0));
    const magnitudeB = Math.sqrt(b.reduce((sum, bi) => sum + bi * bi, 0));

    return dotProduct / (magnitudeA * magnitudeB);
  }
}

export const vectorStore = new VectorStore();

