import React from "react";
import { FileUpload } from "./FileUpload.jsx";
import { useDocuments, useDeleteDocument } from "@/hooks/useDocuments.js";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { ScrollArea } from "@/components/ui/scroll-area";

function DocumentItem({ document }) {
  const deleteMutation = useDeleteDocument();

  const getStatusBadge = (status) => {
    switch (status) {
      case "completed":
        return "status-completed";
      case "processing":
        return "status-processing";
      case "error":
        return "status-error";
      default:
        return "bg-muted";
    }
  };

  const getProgressWidth = (status, progress) => {
    if (progress && typeof progress === 'number') {
      return `${Math.min(100, Math.max(0, progress))}%`;
    }
    switch (status) {
      case "completed":
        return "100%";
      case "processing":
        return "75%";
      case "uploading":
        return "25%";
      case "error":
        return "0%";
      default:
        return "10%";
    }
  };

  const getStatusText = (status, progress) => {
    if (status === "processing" && progress) {
      return `Processing... ${progress}%`;
    }
    switch (status) {
      case "completed":
        return "Ready";
      case "processing":
        return "Processing...";
      case "uploading":
        return "Uploading...";
      case "error":
        return "Error";
      default:
        return "Pending";
    }
  };

  return (
    <div
      className="bg-card border border-border rounded-lg p-4 hover:shadow-sm transition-shadow"
      data-testid={`document-item-${document.id}`}
    >
      <div className="flex items-center space-x-3">
        <div className="w-10 h-10 bg-red-500 rounded-lg flex items-center justify-center flex-shrink-0">
          <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 24 24">
            <path d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" />
          </svg>
        </div>
        <div className="flex-1 min-w-0">
          <p className="text-sm font-medium text-foreground truncate" title={document.filename}>
            {document.filename}
          </p>
          <p className="text-xs text-muted-foreground">
            {(document.fileSize / (1024 * 1024)).toFixed(1)} MB
            {document.totalPages && ` â€¢ ${document.totalPages} pages`}
          </p>
        </div>
        <Button
          variant="ghost"
          size="sm"
          className="text-muted-foreground hover:text-destructive transition-colors p-2 h-auto"
          onClick={() => deleteMutation.mutate(document.id)}
          disabled={deleteMutation.isPending}
          data-testid={`delete-document-${document.id}`}
        >
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        </Button>
      </div>

      <div className="mt-3">
        <div className="w-full bg-muted rounded-full h-2">
          <div
            className={`h-2 rounded-full transition-all duration-300 ${getStatusBadge(document.status)}`}
            style={{ width: getProgressWidth(document.status, document.metadata?.progress) }}
          />
        </div>
        <div className="flex justify-between items-center mt-2">
          <span className="text-xs text-muted-foreground">
            {getStatusText(document.status, document.metadata?.progress)}
          </span>
          {document.totalChunks && (
            <span className="text-xs text-muted-foreground">
              {document.totalChunks} chunks
            </span>
          )}
        </div>
      </div>
    </div>
  );
}

